        mutableStateListOf(
            Habit("Drink Water", "Stay hydrated and fresh"),
            Habit("Read 10 pages", "Daily reading habit"),
            Habit("Exercise", "15 min of body movement")
        )
    }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .background(Color.White)
            .padding(24.dp),
        horizontalAlignment = Alignment.CenterHorizontally
